    const [workstation2Status, setWorkstation2Status] = useState("");
    const [workstation3Status, setWorkstation3Status] = useState("");
    const [totalProgess, setTotalProgress] = useState(0);

    React.useEffect(() => {
        db.collection('Realtime').doc('WS 1').onSnapshot((snapshot) => {
            if (snapshot.data().status !== workstation1Status){
                    setWorkstation1Status({...workstation1Status, progress : progress + 4})
                if(snapshot.data().status === 'Completed')
                    setWorkstation1Status({...workstation1Status, progress : 100})

            }
        })
    },[])

    return{
        workstation1Status,
        workstation2Status,
        workstation3Status,
        totalProgess
    }

}
