import React from 'react';
import { useState, useEffect } from 'react';
import db from '../../../../firebase';
const firebase = require('firebase');
require('firebase/firestore')
require('firebase/auth')

export default function useWorsktationTimeAnalytics(){
    const [workstation1Time, setWorkstation1Time] = useState(0);
    const [workstation2time, setWorkstation2Time] = useState(0);
    const [workstation3Time, setWorkstation3Time] = useState(0);
    const [currentTimes, setCurrentTimes] = useState([
        {
            orderTime: 0,
            workstation1Timestamp: 0,
            workstation2Timestamp: 0,
            workstation3Timestamp : 0
        }
    ])
    
    React.useEffect(() => {
        
        
        db.collection("Order").orderBy("Time", "desc").limit(1).onSnapshot((snapshot) => {
            let OrderTime = snapshot.docs[0].data().Time;
            setCurrentTimes({...currentTimes, orderTime : OrderTime})
        });

        db.collection('Realtime').onSnapshot((snapshot) => {
            console.log(snapshot.docs[0].data().timeStamp + " " +snapshot.docs[1].data().timeStamp + " " +snapshot.docs[2].data().timeStamp)
            setCurrentTimes({...currentTimes, workstation1Timestamp : snapshot.docs[0].data().timeStamp,workstation2Timestamp : snapshot.docs[1].data().timeStamp,workstation3Timestamp : snapshot.docs[2].data().timeStamp });

        })

        currentTimes.forEach((doc) => {
            console.log(doc)
        })

    }, [])

    return(
        workstation1Time,
        workstation2time,
        workstation3Time
    )
}
