import React from 'react';
import { useState, useEffect } from 'react';
import db from '../../../../firebase';
const firebase = require('firebase');
require('firebase/firestore')
require('firebase/auth')

export default function useWorsktationTimeAnalytics(){
    const [workstation1Time, setWorkstation1Time] = useState(0);
    const [workstation2time, setWorkstation2Time] = useState(0);
    const [workstation3Time, setWorkstation3Time] = useState(0);
    
    const [currentTimes, setCurrentTimes] = useState(
        {
           
            workstation1Timestamp: new Date(),
            workstation2Timestamp: new Date(),
            workstation3Timestamp : new Date()
        }
    )
    React.useEffect(() => {
   
        
        db.collection("Order").orderBy("Time", "desc").limit(1).onSnapshot((snapshot) => {
            setOrderTime( new Date(snapshot.docs[0].data().Time.seconds))
        });

        db.collection('Realtime').onSnapshot((snapshot) => {
            //console.log(snapshot.docs[0].data().timeStamp + " " +snapshot.docs[1].data().timeStamp + " " +snapshot.docs[2].data().timeStamp)
            setCurrentTimes({...currentTimes, workstation1Timestamp :  new Date(snapshot.docs[0].data().timeStamp.seconds) ,workstation2Timestamp  : new Date(snapshot.docs[1].data().timeStamp.seconds),workstation3Timestamp : new Date(snapshot.docs[2].data().timeStamp.seconds)});
            
        })
        
       console.log(currentTimes.workstation2Timestamp);
     

    }, [])
    
    return(
        workstation1Time,
        workstation2time,
        workstation3Time
    )
}
